mapper.select:
select cust_id, cust_name, cust_user_id, cust_create_id, b.dict_item_name cust_source, c.dict_item_name cust_industry, d.dict_item_name cust_level, cust_linkman, cust_phone, cust_mobile, cust_createtime from customer a left join( select dict_id, dict_item_name from dict where dict_type_code='002' ) b on a.cust_source = b.dict_id left join( select dict_id, dict_item_name from dict where dict_type_code='001' ) c on a.cust_industry = c.dict_id left join( select dict_id, dict_item_name from dict where dict_type_code='003' ) d on a.cust_level = d.dict_id
cust_name like "%"#{cust_name}"%"
and cust_source=#{cust_source}
and cust_industry=#{cust_industry}
and cust_level=#{cust_level}
limit #{start},#{rows}

select count(*) from customer

	
		cust_name like "%"#{cust_name}"%"
	
	
		and cust_source=#{cust_source}
	
	
		and cust_industry=#{cust_industry}
	
	
		and cust_level=#{cust_level}
